import Header from "./Header";
import Meta from './Meta'

// import matter from "gray-matter";
// import ReactMarkdown from "react-markdown";

class SummaryPage extends React.Component {

    constructor(props) {
        super(props);
        const frontmatter = props.data;
        const markdownBody = props.content;

        this.siteTitle = props.siteTitle || '';
        this.siteDescription = props.siteDescription || '';
        this.classes = props.pathname == "info" && "info_page";
        this.backgroundColor = props.bgColor && props.bgColor;
        this.color = props.pathname == "info" && 'white';

    }

    render() {

        return (
            <>
                <Meta
                    siteTitle={this.siteTitle}
                    siteDescription={this.siteDescription}
                />
                <Header className={`${this.classes} test`} siteTitle={this.siteTitle} />
                <main className="container">{this.props.children}</main>
                <div id="header" className="text-6xl">{this.props.headInfo}</div>
                <div id="resume"><a href="./gc_resume.pdf">Resume (PDF Download)</a></div>
                <div id="about_me">{this.props.contentOne}</div>
                <div id="clients">{this.props.contentTwo}</div>
                <div id="skills">{this.props.contentThree}</div>
                <div id="footer">
                    <div id="contact">{this.props.contactInfo}</div>
                </div>
            </>
        )
    }
};


export default SummaryPage;
